import React,{Component} from "react";
import DjEntries from "./dj_entries";
class MultipleDj extends Component{

    constructor(props){
        super(props);

        this.state = {
            items: [],
            input:''
        };

        this.addItem = this.addItem.bind(this);
    }

    onUpdate(e){

        this.setState({input: e.target.value})

        console.log(this.state.input)

    }

    addItem(e){

        
        console.log(e)

        if (this.state.in !== "")
        {
            console.log('if state')
            var newItem ={
                text:this._inputElement.value,
                key:Date.now()
            };

            this.setState((prevState)=>{
                if(newItem.text !==""){
                    return{
                        items:prevState.items.concat(newItem)
                    };
                }

                
            });

            this.props.updateitems(this.state.items);
        }

        // console.log(this.state.items)

        this._inputElement.value="";
        e.preventDefault();
    }

    render(){
        return(
            <div className="todoListMain">
                
                    <form onSubmit={this.addItem}>
                        <input ref={(a) => this._inputElement = a}
                        placeholder="Enter Task" name='uselessstuff' onChange={(e) => this.onUpdate(e)}/>
                        <button type="submit">add</button>
                    </form>
                
                <DjEntries entries={this.state.items}/>
            </div>
        );
    }
}

export default MultipleDj;
